package whileprog;
import java.util.Scanner;
public class calculation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int i = sc.nextInt();
		if(i%2==1) {
			System.out.println("odd");
		}
		else
			System.out.println("even");
		
			
		}


	}


